mapper.select:
SELECT BG_BOARD_CONTENTS.* FROM BG_BOARD_CONTENTS, BG_BOARD_MASTER WHERE 1 = 1 AND BG_BOARD_MASTER.board_id = #{ board_id } AND BG_BOARD_MASTER.board_id = BG_BOARD_CONTENTS.board_id
AND BG_BOARD_CONTENTS.title LIKE '%${ search }%'
LIMIT #{ rownum }, #{ perpage }

mapper.update:
UPDATE BG_BOARD_CONTENTS
TITLE= #{ title }, CONTENTS = #{ contents }, MOD_DT = NOW()
WHERE CONTENTS_ID = #{ contents_id }

UPDATE BG_BOARD_COMMENTS
COMMENT = #{ comment }, MOD_DT = NOW()
WHERE COMMENT_ID = #{ comment_id }

UPDATE BG_BOARD_CONTENTS
HITS = HITS + 1
WHERE CONTENTS_ID = #{ contents_id }

UPDATE BG_BOARD_COMMENTS
DEL_YN = 'Y', MOD_DT = NOW()
WHERE COMMENT_ID = #{ comment_id }
