mapper.select:
SELECT
MEMBER_ID
FROM
BG_MEMBER
WHERE
	MEMBER_NM = #{ member_nm }


		AND MEMBER_TEL = #{ member_tel }


		AND MEMBER_EMAIL = #{ member_email }


		AND MEMBER_GENDER = #{ member_gender }
		AND MEMBER_YEAR = #{ member_year }
		AND MEMBER_MONTH = #{ member_month }
		AND MEMBER_DAY = #{ member_day }

SELECT
MEMBER_EMAIL
FROM
BG_MEMBER
WHERE
	MEMBER_ID = #{ member_id }
	AND MEMBER_NM = #{ member_nm }


		AND MEMBER_TEL = #{ member_tel }


		AND MEMBER_EMAIL = #{ member_email }


		AND MEMBER_GENDER = #{ member_gender }
		AND MEMBER_YEAR = #{ member_year }
		AND MEMBER_MONTH = #{ member_month }
		AND MEMBER_DAY = #{ member_day }

mapper.update:
UPDATE BG_MEMBER
MEMBER_ID = #{ member_id }, MEMBER_NM = #{ member_nm }, MEMBER_PW = password(#{ member_pw }), MEMBER_EMAIL = #{ member_email }, MEMBER_CEL = #{ member_cel }, MEMBER_ADDR_1 = #{ member_addr_1 }, MEMBER_ADDR_2 = #{ member_addr_2 }, MEMBER_GENDER = #{ member_gender }, MEMBER_YEAR = #{ member_year }, MEMBER_MONTH = #{ member_month }, MEMBER_DAY = #{ member_day }, MOD_DT = NOW()
WHERE MEMBER_ID = #{ member_id }

mapper.select:
SELECT MEMBER_ID
FROM BG_MEMBER
WHERE MEMBER_ID = #{ member_id }
